import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import $ from 'jquery';
import Popper from 'popper.js';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import './plantstats.css'
import Navigation from "../../Navigation/Navigation";
import FinalNav from "../../Navigation/FinalNav";

class PlantStats extends Component{

    constructor(props) {
        super(props);
        this.state = {
            number : 14,
            current : 0,
            startDate : null,
            endDate : null,
            name: 'Surya',
        };

        this.onNumberFixed = this.onNumberFixed.bind(this);
        this.onCycleFixed = this.onCycleFixed.bind(this);
        this.onStartDateFixed = this.onStartDateFixed.bind(this);
        this.onEndDateFixed = this.onEndDateFixed.bind(this);
    }

    pr = {
        cycle : `<div><span>Current Cycle</span><strong id='hel'>${this.props.number}</strong></div>`,
        select : `<div><span>Select cycle</span><select name='cycleNumber' onChange=${this.props.onCycleFixed} > ${this.props.items}</select></div>`,
        startDate : "<div class='input-group-prepend'><span>StartDate</span><strong id='hel'>01/06/2019</strong></div>",
        endDate : "<div class='input-group-prepend'><span>ExpectedEndDate</span><strong id='hel'>15/06/2019</strong></div>",
    };


    render() {
        let items = [];
        for (let i=1;i<this.state.number;i++) {
            items.push(<option value={i}>{i}</option>);
        }
        items.push(<option value={this.state.number} selected>{this.state.number}</option>);
        console.log(items);
        return(
            <div>
                <FinalNav name={this.state.name} pr={this.pr} number={this.state.number} items={items} onCycleFixed={this.onCycleFixed}/>
                <div id="hello1">
                    <p>The Graph comes here</p>
                    <canvas id="myGraph">
                    </canvas>
                </div>
            </div>
        );
    }

    onNumberFixed(event) {
        this.setState({
            number : event.target.value,
        });
    }

    onCycleFixed(event) {
        this.setState({
            current : event.target.value,
        });
    }

    onStartDateFixed(event) {
        this.setState({
            startDate : event.target.value,
        })
    }
    onEndDateFixed(event) {
        this.setState({
            endDate : event.target.value,
        })
    }



}

export default PlantStats;





