import React,{Component} from 'react';
import PlantForm from './PlantForm';

class CreatePlant extends Component{

    constructor(props) {
        super(props);
        this.state = {
            blockname : [],
            totalrows : [],
            zone : [],
            inverter : [],
            smb : [],
            _40mrows : [],
            _20mrows : [],
            wp : [],
            totalmodules : [],
        };
        this.setStateSynchronous = this.setStateSynchronous.bind(this);
    }

    setStateSynchronous(stateUpdate) {
        return new Promise(resolve => {
            this.setState(stateUpdate, () => resolve());
        });
    }

    addAnotherRow(e, values){
        const { blockname,totalrows,zone,inverter,smb,_40mrows,_20mrows,wp } = values;
        let totalmods = _40mrows*40 + _20mrows*20;

        this.setState({
            blockname : this.state.blockname.push(blockname),
            totalrows : this.state.totalrows.push(totalrows),
            zone : this.state.zone.push(zone),
            inverter : this.state.inverter.push(inverter),
            smb : this.state.smb.push(smb),
            _40mrows : this.state._40mrows.push(_40mrows),
            _20mrows : this.state._20mrows.push(_20mrows),
            wp : this.state.wp.push(wp),
            totalmodules : this.state.totalmodules.push(totalmods),
        })
            .then(this.state);
    }

    render() {
        return (
            <div id='hello'>
                <PlantForm
                    AddHandler={this.addAnotherRow}
                />
            </div>
        );
    }
}

export default CreatePlant;
