import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import {Link} from 'react-router-dom';
import './FinalNav.css';
import 'hamburgers/dist/hamburgers.css';
import 'hamburgers/index'
import 'font-awesome/css/font-awesome.css'

class FinalNav extends Component{

    constructor(props) {
        super(props);
        this.state = {
            plant : '',
            html1 : "",
            html2 : "",
            html3 : "",
        };
        this.OnPlantChange = this.OnPlantChange.bind(this);
        this.SelectCycle = this.SelectCycle.bind(this);
        this.SelectStartDate = this.SelectStartDate.bind(this);
        this.SelectEndDate = this.SelectEndDate.bind(this);
    }

    static Hello(){
            document.getElementById('sidebar').classList.toggle('active');
    }

    SelectCycle(){
        let html = this.props.pr.cycle;
        let html1 = html.replace('undefined',this.props.number);
        this.setState({
            html1 : html1,
        })
    }

    SelectStartDate(){
        let html = this.props.pr.startDate;
        let html1 = html.replace('undefined',this.props.startDate);
        this.setState({
            html2 : html1,
        })
    }

    SelectEndDate(){
        let html = this.props.pr.endDate;
        let html1 = html.replace('undefined',this.props.endDate);
        this.setState({
            html3 : html1,
        })
    }

    componentDidMount() {
        setInterval(this.SelectCycle, 500);
        setInterval(this.onChange,100);
        if(this.props.plantstats){
            setTimeout(this.SelectStartDate, 500);
            setTimeout(this.SelectEndDate, 500);
        }
    }

    componentDidUpdate(prevProps, prevState, snapshot) {
        if(prevState !== this.state){
            if(this.props.role === 'admin')
            {
                fetch("http://localhost:3000/check-cycle",{
                    method : 'post',
                    headers : {
                        'Content-Type' : 'application/json',
                    },
                    body : JSON.stringify({
                        plant : this.state.plant,
                    })
                })
                    .then(resp=> resp.json())
                    .then( hell =>
                    {
                        if(hell['body'] === 1){
                            const p = prompt('The Cycle has been completed,shall you want the cycle to start from today. Enter Y to start');
                            if(p === 'Y')
                            {
                                const a = prompt('The Cycle will be completed within:(no of days)');
                                fetch("http://localhost:3000/create-cycle",
                                    {
                                        method : 'post',
                                        headers : {
                                            'Content-Type' : 'application/json',
                                        },
                                        body : JSON.stringify({
                                            plant : this.state.plant,
                                            startToday : hell['body'],
                                            estimatedDuration : a,
                                        })
                                    }
                                ).then(data => alert('Success'))
                            }
                        }
                    })
                    .catch(err=>console.log(err));
            }
        }
    }

    render(){
        let plant_array = this.props.plants.split('&');
        let items5 = [];
        for(let i=0;i<plant_array.length-1;i++){
            items5.push(<option key={plant_array[i]} value={plant_array[i]}>{plant_array[i]}</option>);
        }
        return (
            <div className="wrapper">
                <nav id="sidebar">
                    <ul className="list-unstyled components">
                        <li className="nav-item active">
                            <strong className="nav-link" >Current Plant:<span className="sr-only">(current)</span></strong>
                        </li>
                        <li id="bellow" className="nav-item active">
                            <strong className="nav-link" >{this.state.plant.toUpperCase()}<span className="sr-only">(current)</span></strong>
                        </li>
                        <li className="active">
                            <label id="bello" htmlFor="sel1">Select Plant:</label>
                            <select onChange={this.OnPlantChange} className="form-control" id="sel1">
                                {items5}
                            </select>
                        </li>
                        <li>
                            <Link to='/plantstats'>Plant Stats</Link>
                        </li>
                        <li>
                            <Link to='/rowstats'>Row Stats</Link>
                        </li>
                        <li>
                            <Link to='/zonestats'>Zone Stats</Link>
                        </li>
                    </ul>
                    <ul className="list-unstyled LTS" id='kelo'>
                        <li>
                            <i id='profile' className="fa fa-user fa-2x"></i>
                        </li>
                        <li>
                            <h5>{this.props.fname}</h5>
                        </li>
                        <li>
                            <h5>{this.props.role}</h5>
                        </li>
                        <li>
                            <h5 id='compa'>SSAEL</h5>
                        </li>
                        <li>
                            <a className='download' href="http://localhost:5000/signin">Log Out</a>
                        </li>
                    </ul>
                </nav>
                <nav id="hell" className="navbar navbar-expand-lg navbar-dark bg-dark">
                    <div>
                        <button className="btn btn-light"
                                type="button"
                                id="sidebarCollapse"
                                onClick={FinalNav.Hello}>
                            <i className="fa fa-align-justify" />
                        </button>
                        <strong className='navbar-brand'>QR App</strong>
                    </div>
                    <button className="navbar-toggler"
                            type="button"
                            data-toggle="collapse"
                            data-target="#navbarSupportedContent"
                            aria-controls="navbarSupportedContent"
                            aria-expanded="false"
                            aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <div className="collapse navbar-collapse"
                         id="navbarSupportedContent">
                        <ul className="navbar-nav ml-auto">
                            <li className="nav-item active">
                                <strong className="nav-link"
                                        dangerouslySetInnerHTML = {{ __html: this.state.html1 }}>
                                </strong>
                            </li>
                            <li className="nav-item active">
                                <strong className="nav-link"
                                        dangerouslySetInnerHTML = {{ __html: this.state.html2 }}>
                                </strong>
                            </li>
                            <li className="nav-item active">
                                <strong className="nav-link"
                                        dangerouslySetInnerHTML = {{ __html: this.state.html3 }}>
                                </strong>
                            </li>
                        </ul>
                    </div>
                </nav>
            </div>
        );
    }

    OnPlantChange(event) {
        this.setState({
            plant : event.target.value.toLowerCase(),
        });
    }
}

export default FinalNav;
