import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import $ from 'jquery';
import Popper from 'popper.js';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import './plantstats.css'
import FinalNav from "../../Navigation/FinalNav";
import {Redirect, withRouter} from "react-router";

class PlantStats extends Component{

    constructor(props) {
        super(props);
        this.state = {
            number: 14,
            current: 0,
            startDate: '16/10/2019',
            endDate: '1/11/2019',
            redirect : false,
        };
    }

    pr = {
        cycle : `<div><span>Current Cycle - </span><strong>${this.props.number}</strong></div>`,
        select : ``,
        startDate : "<div><span>StartDate - </span><strong>"+this.props.startDate+"</strong></div>",
        endDate : "<div><span>ExpectedEndDate - </span><strong>"+this.props.endDate+"</strong></div>",
    };

    componentDidMount() {
        if(!this.props.signedIn){
            this.setState({
                redirect : true,
            });
        }
        if(this.props.role === 'admin')
        {
            fetch("http://localhost:3000/check-cycle",{
                method:'get',
                }
            )
                .then(resp=> resp.json())
                .then( hell =>
                {
                    if(hell['body'] === 1){
                        const p = prompt('The Cycle has been completed,shall you want the cycle to start from today. Enter Y to start');
                        if(p === 'Y')
                        {
                            const a = prompt('The Cycle will be completed within:(no of days)');
                            fetch("http://localhost:3000/create-cycle",
                                {
                                    method : 'post',
                                    headers : {
                                        'Content-Type' : 'application/json',
                                    },
                                    body : JSON.stringify({
                                        startToday : hell['body'],
                                        estimatedDuration : a,
                                    })
                                }
                            )
                                .then(data => alert('Success'))
                        }
                    }
                })
                .catch(err=>console.log(err));
        }

        fetch('http://localhost:3000/get-cycle-data')
            .then(res=>res.json())
            .then(data=>{
                let { startDate,endDate,currentCycle } = data;
                this.setState({
                    startDate: startDate,
                    endDate: endDate,
                    number: currentCycle,
                })
                document.location.reload();
            })
    }

    render() {
        let items = [];
        for (let i=1;i<this.state.number;i++) {
            items.push(<option value={i}>{i}</option>);
        }
        items.push(<option value={this.state.number} selected>{this.state.number}</option>);
        if(this.state.redirect){
            alert("SignIn to Access the Info");
            return <Redirect to='/signin'/>
        }
        return(
            <div>
                <FinalNav fname={this.props.fname}
                          role={this.props.role}
                          plants={this.props.plants}
                          pr={this.pr}
                          number={this.state.number}
                          startDate={this.state.startDate}
                          endDate={this.state.endDate}
                          plantstats={true}
                />
                <div id="hello1">
                    <div><span><strong>Select cycle</strong></span><select className="form-control" name='cycleNumber' onChange={this.onCycleFixed}> {items}</select></div><br/>
                    <p>The Graph comes here</p>
                    <canvas id="myGraph">
                    </canvas>
                </div>
            </div>
        );
    }

}

export default withRouter(PlantStats);
