import React from 'react';
import clsx from 'clsx';
import { makeStyles } from '@material-ui/core/styles';
import MenuItem from '@material-ui/core/MenuItem';
import {TextField, Button} from '@material-ui/core';
import {AddCircle} from "@material-ui/icons";

const useStyles = makeStyles(theme => ({
    container: {
        display: 'flex',
        flexWrap : 'wrap',
    },
    textField: {
        margin : 4,
        width : 191,
    },
    div : {
        margin : 10,
        border: 2,
        borderColor: 'black',
        borderStyle : "solid",
    }
}));

export default function PlantForm({ AddHandler }) {
    const classes = useStyles();
    const [values, setValues] = React.useState({
        blockname : '',
        totalrows : 0,
        zone : 0,
        inverter : '',
        smb : '',
        _40mrows : 0,
        _20mrows : 0,
        wp : 0,
        totalmodules : 0,
    });

    const handleChange = name => event => {
        setValues({...values, [name]: event.target.value});
    };

    return (
        <div className={classes.div}>
            <form className={classes.container} autoComplete="off">
                <TextField
                    required
                    id="outlined-required"
                    label="Block Name"
                    defaultValue="Block name"
                    className={classes.textField}
                    margin="normal"
                    onChange={handleChange('blockname')}
                />
                <TextField
                    required
                    id="outlined-required"
                    label="Total Rows"
                    defaultValue="Total Rows"
                    className={classes.textField}
                    margin="normal"
                    onChange={handleChange('totalrows')}
                />
                <TextField
                    required
                    id="outlined-required"
                    label="Zone"
                    defaultValue='Zone'
                    className={classes.textField}
                    margin="normal"
                    onChange={handleChange('zone')}
                />
                <TextField
                    required
                    id="outlined-required"
                    label="Inverter"
                    defaultValue='Inverter'
                    className={classes.textField}
                    margin="normal"
                    onChange={handleChange('inverter')}
                />
                <TextField
                    required
                    id="outlined-required"
                    label="SMB"
                    defaultValue='SMB'
                    className={classes.textField}
                    margin="normal"
                    onChange={handleChange('smb')}
                />
                <TextField
                    required
                    id="outlined-required"
                    label="FortyRows"
                    defaultValue='Forty Rows'
                    className={classes.textField}
                    margin="normal"
                    onChange={handleChange('_40mrows')}
                />
                <TextField
                    required
                    id="outlined-required"
                    label="TwentyRows"
                    defaultValue='Twenty Rows'
                    className={classes.textField}
                    margin="normal"
                    onChange={handleChange('_20mrows')}
                />
                <TextField
                    required
                    id="outlined-required"
                    label="Watt Power"
                    defaultValue='Watt Power'
                    className={classes.textField}
                    margin="normal"
                    onChange={handleChange('wp')}
                />
                <Button
                    onClick={AddHandler}
                >
                    <AddCircle/>
                </Button>
            </form>
        </div>
    );
}
