import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import $ from 'jquery';
import Popper from 'popper.js';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import './textfields.css';

class Register extends Component{
    constructor(props) {
        super(props);
        this.state =
            {
                FullNameChange : "",
                RoleChange : "",
                EmailChange : "",
                UserNameChange : "",
                PlantsChange : "",
            };
        this.onFullChange = this.onFullChange.bind(this);
        this.onRoleChange = this.onRoleChange.bind(this);
        this.onEmailChange = this.onEmailChange.bind(this);
        this.onUserNameChange = this.onUserNameChange.bind(this);
        this.onSubmitClicked = this.onSubmitClicked.bind(this);
    }

    render() {
        return (
            <div>
                <form className='hello'>
                    <div className="form-group">
                        <label htmlFor="exampleInputFullName1">Full Name</label> <br />
                        <input type="text"
                               name="fname"
                               className="form-control"
                               id="exampleInputFullName1"
                               aria-describedby="emailHelp"
                               placeholder="Full Name"
                               onChange={this.onFullChange}
                        />
                        <br />
                        <label htmlFor="exampleInputRole1">Role</label><br />
                        <input type="text"
                               name="role"
                               className="form-control"
                               id="exampleInputRole1"
                               placeholder="Role"
                               onChange={this.onRoleChange}
                        />
                        <br />
                        <label htmlFor="exampleInputEmail1">email</label><br />
                        <input type="email"
                               name="email"
                               className="form-control"
                               id="exampleInputEmail1"
                               placeholder="Email"
                               onChange={this.onEmailChange}
                        />
                        <br />
                        <label htmlFor="exampleInputPassword1">User Name</label><br />
                        <input type="text"
                               name="uid"
                               className="form-control"
                               id="exampleInputPassword1"
                               placeholder="User Name"
                               onChange={this.onUserNameChange}
                        />
                        <br />
                        <form>
                            <h3>Select your favorite sports:</h3>
                            <label><input type="checkbox" value="football" name="sport" />Football</label>
                            <label><input type="checkbox" value="baseball" name="sport" />Baseball</label>
                            <label><input type="checkbox" value="cricket" name="sport" />Cricket</label>
                            <label><input type="checkbox" value="boxing" name="sport" />Boxing</label>
                            <label><input type="checkbox" value="racing" name="sport" />Racing</label>
                            <label><input type="checkbox" value="swimming" name="sport" />Swimming</label>
                            <br />
                                <button type="button">Get Values</button>
                        </form>
                        <br/>
                        <input type='submit' onClick={this.onSubmitClicked} value="Sign In" className='btn btn-success' />
                    </div>
                </form>
            </div>
        );
    }

    onFullChange(event) {
        this.setState(
            {FullNameChange : event.target.value},
        );
    }

    onRoleChange(event) {
        this.setState(
            {RollChange : event.target.value},
        );
    }

    onEmailChange(event) {
        this.setState(
            {EmailChange : event.target.value},
        );
    }

    onUserNameChange(event) {
        this.setState(
            {UserNameChange : event.target.value},
        );
    }

    onPlantsChecked(e) {
        var checkboxes = document.getElementsByName('location');
        var vals = "";
        for (var i=0, n=checkboxes.length;i<n;i++)
        {
            if (checkboxes[i].checked)
            {
                vals += ","+checkboxes[i].value;
            }
        }
        if (vals) vals = vals.substring(1);
    }

    onSubmitClicked = (e) => {
        e.preventDefault();
        fetch('http://localhost:3000/register',
            {
                method: "post",
                headers :{
                    "Content-Type" : 'application/json',
                },
                body: JSON.stringify({
                    username : this.state.UsernameChange,
                    password : this.state.passwordChange
                })
            })
            .then(resp => resp.json())
            .then(data => {
                if(data['status'] === 'success')
                {
                    this.props.handler(this.state);
                }
            });

    }
}

export default Register;


// fname, role, email, uid, plants, password

<div className="input-group mb-3">
    <div className="input-group-prepend">
        <div className="input-group-text">
            <input type="checkbox" value="football" name="sport" />
        </div>
    </div>
    <em type="text" className="form-control" aria-label="Text input with checkbox"></em>
</div>
