import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import 'tachyons';
import FinalNav from "../../Navigation/FinalNav";
import {Redirect, withRouter} from "react-router";
import Material_Card from '../../Card/Material_Card';
import './zonestats.css';
import ZoneDetails from '../../Card/ZoneDetailCard';
import {Button} from "@material-ui/core";

class BlockStats extends Component {

    constructor(props) {
        super(props);
        this.state = {
            zone: 0,
            cycle: 0,
            current: 1,
            row: 0,
            redirect: false,
            plant: this.props.plants.split('&')[0].toLowerCase(),
            startDate: '',
            fmod : 0,
            cleanstatus : 0,
            updateDate: '',
            updateTime: '',
            comments : '',
        };

        this.handler2 = this.handler2.bind(this);
        this.setPlant = this.setPlant.bind(this);
        this.setStateSynchronous = this.setStateSynchronous.bind(this);
    }

    setStateSynchronous(stateUpdate) {
        return new Promise(resolve => {
            this.setState(stateUpdate, () => resolve());
        });
    }

    pr = {
        cycle: "",
        select: "",
        startDate: "",
    };

    componentDidMount() {
        if (!this.props.signedIn) {
            this.setState({
                redirect: true,
            });
        }

        fetch("http://localhost:3000/get-cycle-data", {
            method: 'post',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                plant: this.state.plant,
            }),
        })
            .then(res => res.json())
            .then(result => {
                this.setState({
                    startDate: result.startDate,
                    cycle: result.currentCycle
                });
            });

    }

    handler2(number) {
        this.setState({
            number: number,
        });
    }

    setPlant(plant) {
        this.setState({
            plant: plant,
        });
    }

    handler3() {
        let body = document.getElementById('least');
        body.classList.toggle('fill');
    }

    render() {
        if (this.state.redirect) {
            alert("SignIn to Access the Info");
            return <Redirect to='/signin'/>
        }
        return (
            <div>
                <FinalNav fname={this.props.fname}
                          role={this.props.role}
                          plants={this.props.plants}
                          pr={this.pr}
                          handler1={this.handler2}
                          number={this.state.zone}
                          plantstats={false}
                          handle={this.handler3}
                          setplant={this.setPlant}
                />
                <div id='least' className='fill least'>
                </div>
                <div id='keast' className='fill surya least'>
                    {this.state.details}
                    <Button onClick={this.onButtonClicked} variant="contained" className="outlined-button" color="primary">
                        Get Data For another Zone
                    </Button>
                </div>
            </div>
        );
    }
}

export default withRouter(BlockStats);
