import React, { Component } from 'react';
import Navigation from './Navigation/Navigation';
import TextFields from './TextFields/textfields';
import Root from './Root/Root'
import './App.css';
import PlantStats from './Stats/plantstats/plantstats';


class App extends Component {

    constructor(props)
    {
        super(props);
        this.state = {
            route: 'signIn',
            signedIn : false,
            text : 'Sign In'
        };
        this.onRouteChange = this.onRouteChange.bind(this);
        this.onRouteChange1 = this.onRouteChange1.bind(this);
    }

    onRouteChange(name) {
        this.setState({
           route : name,
        });
        if(this.state.signedIn){
            this.setState({
                text : 'Sign Out',
            })
        }
    }
    onRouteChange1(name) {
        this.setState({
           route : name,
            signedIn: true,
        });
    }

    render() {
    return (
      <div className="App">

          {(() => {
              switch(this.state.route) {
                  case 'signIn':
                      return <div>
                                <Navigation onRouteChange={this.onRouteChange} text={this.state.text} />
                                <TextFields onRouteChange={this.onRouteChange}/>
                            </div>;
                  case 'plant':
                      return <div>
                             <Navigation onRouteChange={this.onRouteChange1} text={this.state.text}/>
                            <PlantStats />
                          </div>;
                  case 'root':
                      return <Root onRouteChange={this.onRouteChange}/>
                  default:
                      return null;
              }
          })()}
      </div>
    );
  }
}

export default App;
