import React, { Component } from 'react';
import './table.css';

class Table extends Component {

    constructor(props) {
        super(props);

        this.state = {
            status : [],
        }
    }


    componentDidMount() {
        fetch('http://192.168.64.2/this.php')
                .then(resp => resp.json())
                .then((data) => {
                    this.setState({
                        status : data['hello'],
                    })
                })
                .catch(err => console.log(err));
    }

    componentDidUpdate(prevProps, prevState, snapshot) {
        console.log(this.state);
    }

    renderTableData()
    {
        return this.state.status.map((tab, index) => {
                const { row, cleanstatus, progress } = tab; //destructuring
                return (
                    <tr key={row}>
                        <td>{row}</td>
                        <td>{cleanstatus}</td>
                        <td>{progress}</td>
                    </tr>
                )
        })
    }

    renderTableHeader() {
        // console.log(this.props.tab[0]);
        return (
            <tr>
                <th>Row</th>
                <th>Clean Status</th>
                <th>Progress</th>
            </tr>
        )
    }

    render() {
        return (
            <div>
                <h1 id='title'>Row Info</h1>
                <table id='RvZ'>
                    <tbody>
                    {this.renderTableHeader()}
                        {this.renderTableData()}
                    </tbody>
                </table>
            </div>
        )
    }
}

export default Table;
