import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import $ from 'jquery';
import Popper from 'popper.js';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import './plantstats.css'
import FinalNav from "../../Navigation/FinalNav";
import {Redirect, withRouter} from "react-router";
import {LineChart, Line, Legend, AreaChart, Area, Brush, XAxis, YAxis, CartesianGrid, Tooltip} from 'recharts';

class PlantStats extends Component{

    constructor(props) {
        super(props);
        this.state = {
            number: 14,
            current: 0,
            startDate: '16/10/2019',
            endDate: '1/11/2019',
            redirect : false,
            plant : this.props.plants.split('&')[0].toLowerCase(),
            modulesCleaned : 0,
            data : [
                {
                    "name": "Page A",
                    "uv": 4000,
                    "pv": 2400,
                    "amt": 2400
                },
                {
                    "name": "Page B",
                    "uv": 3000,
                    "pv": 1398,
                    "amt": 2210
                },
                {
                    "name": "Page C",
                    "uv": 2000,
                    "pv": 9800,
                    "amt": 2290
                },
                {
                    "name": "Page D",
                    "uv": 2780,
                    "pv": 3908,
                    "amt": 2000
                },
                {
                    "name": "Page E",
                    "uv": 1890,
                    "pv": 4800,
                    "amt": 2181
                },
                {
                    "name": "Page F",
                    "uv": 2390,
                    "pv": 3800,
                    "amt": 2500
                },
                {
                    "name": "Page G",
                    "uv": 3490,
                    "pv": 4300,
                    "amt": 2100
                }
            ],
        };
        this.onCycleFixed = this.onCycleFixed.bind(this);
        this.setStateSynchronous = this.setStateSynchronous.bind(this);
        this.handler3 = this.handler3.bind(this);
        this.setPlant = this.setPlant.bind(this);
    }

    setStateSynchronous(stateUpdate) {
        return new Promise(resolve => {
            this.setState(stateUpdate, () => resolve());
        });
    }

    pr = {
        cycle : `<div><span>Current Cycle - </span><strong>${this.props.number}</strong></div>`,
        select : ``,
        startDate : "<div><span>StartDate - </span><strong>"+this.props.startDate+"</strong></div>",
        endDate : "<div><span>ExpectedEndDate - </span><strong>"+this.props.endDate+"</strong></div>",
    };

    componentDidMount() {
        if (!this.props.signedIn) {
            this.setState({
                redirect: true,
            });
        }
        if(this.props.role === 'admin')
        {
            fetch("http://localhost:3000/check-cycle",{
                method : 'post',
                headers : {
                    'Content-Type' : 'application/json',
                },
                body : JSON.stringify({
                    plant : this.state.plant,
                })
            })
                .then(resp=> resp.json())
                .then( hell =>
                {
                    console.log(hell);
                    if(hell['body'] === 1){
                        const p = prompt('The Cycle has been completed,shall you want the cycle to start from today. Enter Y to start');
                        if(p === 'Y')
                        {
                            const a = prompt('The Cycle will be completed within:(no of days)');
                            fetch("http://localhost:3000/create-cycle",
                                {
                                    method : 'post',
                                    headers : {
                                        'Content-Type' : 'application/json',
                                    },
                                    body : JSON.stringify({
                                        plant : this.state.plant,
                                        startToday : hell['body'],
                                        estimatedDuration : a,
                                    })
                                }
                            ).then(data => alert('Success'))
                        }
                    }
                })
                .catch(err=>console.log(err));
        }
        fetch('http://localhost:3000/get-cycle-data',{
            method : 'post',
            headers : {
                'Content-Type' : 'application/json',
            },
            body : JSON.stringify({
                plant : this.state.plant,
            })
        })
            .then(res=>res.json())
            .then(data=>{
                let { startDate,endDate,currentCycle } = data;
                this.setState({
                    startDate: startDate,
                    endDate: endDate,
                    number: currentCycle,
                });
            });
    }

    handler3(){
        let body = document.getElementById('hello1');
        body.classList.toggle('fill');
    }

    setPlant(plant){
        this.setState({
            plant : plant,
        });
    }

    render() {
        let items = [];
        for (let i=1;i<this.state.number;i++) {
            items.push(<option value={i}>{i}</option>);
        }
        items.push(<option value={this.state.number} selected>{this.state.number}</option>);
        if(this.state.redirect){
            alert("SignIn to Access the Info");
            return <Redirect to='/signin'/>
        }

        return(
            <div>
                <FinalNav fname={this.props.fname}
                          role={this.props.role}
                          plants={this.props.plants}
                          pr={this.pr}
                          handler = {this.props.handler}
                          number={this.state.number}
                          startDate={this.state.startDate}
                          endDate={this.state.endDate}
                          plantstats={true}
                          handle={this.handler3}
                          setplant={this.setPlant}
                />
                <div id="hello1" className='fill'>
                    <div><span><strong>Select cycle</strong></span><select className="form-control" name='cycleNumber' onChange={this.onCycleFixed}> {items}</select></div><br/>
                    <LineChart width={730} height={250} data={this.state.data}
                               margin={{ top: 5, right: 30, left: 20, bottom: 5 }}>
                        <CartesianGrid strokeDasharray="3 3" />
                        <XAxis dataKey="name" />
                        <YAxis />
                        <Tooltip />
                        <Legend />
                        <Line type="monotone" dataKey="pv" stroke="#8884d8" />
                        <Line type="monotone" dataKey="uv" stroke="#82ca9d" />
                    </LineChart>
                </div>
            </div>
        );
    }

    onCycleFixed(e) {
        e.preventDefault();
        this.setStateSynchronous(({current: Number(e.target.value)})).then( bro => {
                const date = new Date();
                let date1 = date.getFullYear() + '-' + ('0' + (date.getMonth() + 1)).slice(-2) + '-' + ('0' + date.getDate()).slice(-2);
                console.log(date1);
                fetch('http://localhost:3000/getplant',{
                    method : 'post',
                    headers : {
                        'Content-Type' : 'application/json',
                    },
                    body : JSON.stringify({
                        plant : this.state.plant,
                        startDate : this.state.startDate,
                        presentDate : date1,
                        cycle : this.state.current,
                    })
                })
                    .then(hel => hel.json())
                    .then(lol=>{
                        this.setState({
                            data : lol['data'],

                        })
                    });
        }
        )
    }
}

export default withRouter(PlantStats);
