import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import {Link} from 'react-router-dom';
import './FinalNav.css';
import 'hamburgers/dist/hamburgers.css';
import 'hamburgers/index'
import 'font-awesome/css/font-awesome.css'

class FinalNav extends Component{

    constructor(props) {
        super(props);
        this.state = {
            plant : 'Kothagadi',
            html1 : "",
            html2 : "",
            html3 : "",
        };
        this.OnPlantChange = this.OnPlantChange.bind(this);
        this.SelectCycle = this.SelectCycle.bind(this);
        this.SelectStartDate = this.SelectStartDate.bind(this);
        this.SelectEndDate = this.SelectEndDate.bind(this);
        this.handleResize = this.handleResize.bind(this);
    }

    static Hello(){
            document.getElementById('sidebar').classList.toggle('active');
    }

    SelectCycle(){
        let html = this.props.pr.cycle;
        let html1 = html.replace('undefined',this.props.number);
        this.setState({
            html1 : html1,
        })
    }

    SelectStartDate(){
        let html = this.props.pr.startDate;
        let html1 = html.replace('undefined',this.props.startDate);
        this.setState({
            html2 : html1,
        })
    }

    SelectEndDate(){
        let html = this.props.pr.endDate;
        let html1 = html.replace('undefined',this.props.endDate);
        this.setState({
            html3 : html1,
        })
    }

    componentDidMount() {
        setInterval(this.SelectCycle, 500);
        if(this.props.plantstats){
            setTimeout(this.SelectStartDate, 500);
            setTimeout(this.SelectEndDate, 500);
        }
        window.addEventListener('resize', this.handleResize);
    }



    render(){
        return (
            <div className="wrapper">
                <nav id="sidebar">
                    <div className="sidebar-header">
                        <h3>QR App</h3>
                    </div>
                    <ul className="list-unstyled components">
                        <li className="active">
                            <label htmlFor="sel1">Select Plant:</label>
                            <select onChange={this.OnPlantChange} className="form-control" id="sel1">
                                <option defaultValue>Kothagadi</option>
                                <option>Orai</option>
                                <option>Peerampalli</option>
                                <option>Parigi</option>
                                <option>Rohini</option>
                            </select>
                        </li>
                        <li>
                            {/*<a href="#">Zone Stats</a>*/}
                            <Link to='/plantstats'>Plant Stats</Link>
                        </li>
                        <li>
                            {/*<a href="#">Row Stats</a>*/}
                            <Link to='/rowenquiry'>Row Stats</Link>
                        </li>
                        <li>
                            {/*<a href="#">Zone Stats</a>*/}
                            <Link to='/zonestats'>Zone Stats</Link>
                        </li>
                    </ul>

                    <ul className="list-unstyled CTAs">
                        <li>
                            <a href="#" className="download">Logged in as</a>
                        </li>
                        <li>
                            <a href="#" className="article">{this.props.name}</a>
                        </li>
                    </ul>
                    <ul className="list-unstyled components" id='kelo'>
                        <li>
                            <Link to={"/signin"}>Log Out</Link>
                        </li>
                    </ul>
                </nav>
                <div id='hell'>
                    <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
                            <ul className="navbar-nav">
                                <li className='nav-item active'>
                                    <button type="button" id="sidebarCollapse" className="btn btn-danger" onClick={FinalNav.Hello}>
                                        <i className="fa fa-align-justify"></i> <span>toggle sidebar</span>
                                    </button>
                                </li>
                            </ul>
                            <button className="navbar-toggler" type="button" data-toggle="collapse"
                                    data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent"
                                    aria-expanded="false" aria-label="Toggle navigation">
                                <span className="navbar-toggler-icon"></span>
                            </button>
                        <div class="collapse navbar-collapse" id="navbarSupportedContent">
                            <ul className="navbar-nav ml-auto">
                                <li className="nav-item active">
                                    <strong className="nav-link" >{this.state.plant}<span className="sr-only">(current)</span></strong>
                                </li>
                                <li className="nav-item active">
                                    <strong className="nav-link" dangerouslySetInnerHTML = {{ __html: this.state.html1 }}></strong>
                                </li>
                                <li className="nav-item active">
                                    <strong className="nav-link" dangerouslySetInnerHTML = {{ __html: this.props.pr.select }}></strong>
                                </li>
                                <li className="nav-item active">
                                    <strong className="nav-link" dangerouslySetInnerHTML = {{ __html: this.state.html2 }}></strong>
                                </li>
                                <li className="nav-item active">
                                    <strong className="nav-link" dangerouslySetInnerHTML = {{ __html: this.state.html3 }}></strong>
                                </li>
                            </ul>
                        </div>
                    </nav>
                </div>
            </div>
        );
    }

    OnPlantChange(event) {
        this.setState({
            plant : event.target.value,
        });
        console.log(this.state);
    }

    handleResize() {
        this.setState({
            width : window.innerWidth,
        })
    }
}

export default FinalNav;
//
