import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import $ from 'jquery';
import Popper from 'popper.js';
import 'bootstrap/dist/js/bootstrap.bundle.min';

class plantstats extends Component{

    constructor(props) {
        super(props);
        this.state = {
            number : 0,
            current : 0,
        }
    }


    render() {
        return(
            <div className="input-group mb-3">
                <div className="input-group-prepend">
                    <span className='input-group-text'>Current Cycle</span>
                </div>
                <input type='number' name="currentCycle" onChange={this.onNumberFixed} id="currentCycle" /><br/>
                <input type='number' name="cycleNumber" onChange={this.onCycleFixed()} id="cycleNumber" /><br />
                <canvas id="myGraph"></canvas>

            </div>
        );
    }

    onNumberFixed(event) {
        this.setState({
            number : event.target.value,
        });
    }

    onCycleFixed(event) {
        this.setState({
            current : event.target.value,
        })
    }

}

export default plantstats;
