import React, { Component } from 'react';
import Navigation from './Navigation/Navigation';
import TextFields from './TextFields/textfields';
import Root from './Root/Root'
import './App.css';
import PlantStats from './Stats/plantstats/plantstats';
import {Route} from "react-router";
import {BrowserRouter, Redirect} from "react-router-dom";
import ZoneStats from "./Stats/zonestats/zonestats";
import Rowstats from "./Stats/rowstats/rowstats";
import RowEnquiry from "./Stats/rowstats/rowenquiry";


class App extends Component {

    constructor(props)
    {
        super(props);
        this.state = {
            route: 'signIn',
            signedIn : false,
            text : 'Sign In',
            name: 'Surya',
            redirect : true,
        };
        this.handler = this.handler.bind(this);
    }

    handler(state) {
        this.setState({
            signedIn: true,
        });
        this.handler1(state.UsernameChange);
    }

    handler1(value){
        this.setState({
            name : value,
        })
    }


    render() {
    return (
      <div className="App">
          {
                this.state.signedIn && (this.state.count !== 1) ? <BrowserRouter>
                  <Route path="/" render={(props) => <Navigation name={this.state.name} {...props}/>} />
                  <Route path="/root" component={Root} />
                  <Route path="/signin" component={TextFields} />
                  <Route path="/plantstats" component={PlantStats}/>
                  <Route path="/zonestats" component={ZoneStats} />
                  <Route path="/rowstats" component={Rowstats} />
                  <Route path="/rowenquiry" component={RowEnquiry}/>
              </BrowserRouter>
                  :
                    <BrowserRouter>
                        <Route path='/' render={(props) => <TextFields handler={this.handler} {...props}/>} />
                        <Redirect to='/plantstats'/>
                  </BrowserRouter>
          }
      </div>
    );
  }
}

export default App;
