import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import 'tachyons';
import FinalNav from "../../Navigation/FinalNav";
import Table from './Table';
import {Redirect, withRouter} from "react-router";
import Material_Card from '../../Card/Material_Card';
import './plantstats.css'

class ZoneStats extends Component{

    constructor(props) {
        super(props);
        this.state = {
            zone : [],
            cycle : 0,
            current: 1,
            row : 0,
            totalrows : [],
            redirect : false,
            plant : this.props.plants.split('&')[0].toLowerCase(),
            accap : [],
            dccap : [],
            totalzones : 0,
        };

        this.onNumberFixed = this.onNumberFixed.bind(this);
        this.onCycleFixed = this.onCycleFixed.bind(this);
        this.onRowFixed = this.onRowFixed.bind(this);
        this.handler2 = this.handler2.bind(this);
        this.setPlant = this.setPlant.bind(this);
        this.onCardClicked = this.onCardClicked.bind(this);
        this.setStateSynchronous = this.setStateSynchronous.bind(this);
    }

    setStateSynchronous(stateUpdate) {
        return new Promise(resolve => {
            this.setState(stateUpdate, () => resolve());
        });
    }

    pr = {
        cycle : "",
        select : "",
        startDate : "",
    };

    componentDidMount() {
        if(!this.props.signedIn){
            this.setState({
                redirect : true,
            });
        }

        fetch("http://localhost:3000/get-cycle-data",{
            method : 'post',
            headers : {
                'Content-Type' : 'application/json',
            },
            body : JSON.stringify({
                plant : this.state.plant,
            }),
        })
            .then(res => res.json())
            .then(result => {
                this.setState({
                    cycle : result.currentCycle,
                });
            })
    }

    handler2(number){
        this.setState({
            number: number,
        });
    }

    setPlant(plant){
        this.setState({
            plant : plant,
        });
    }

    handler3(){
        let body = document.getElementById('least');
        body.classList.toggle('fill');
    }

    render() {
        if(this.state.redirect){
            alert("SignIn to Access the Info");
            return <Redirect to='/signin'/>
        }
        let items1 = [];
        for (let i=1;i<=this.state.totalrows;i++) {
            items1.push(<option key={i} value={i}>{i}</option>);
        }
        let items2 = [];
        for (let j=this.state.current;j<=this.state.totalrows;j++) {
            items2.push(<option key={j} value={j}>{j}</option>);
        }

        fetch('http://localhost:3000/zonedata',{
            method : 'post',
            headers : {
                'Content-Type' : 'applicatio/json',
            },
            body: JSON.stringify({
                plant : this.state.plant,
            })
        }).then(resp => resp.json())
            .then(hello => {
                console.log(hello);
                hello.map((zo, i) => {
                    this.setState({
                        zone : this.state.zone.push(zo['zone']),
                        accap : this.state.accap.push(zo['accapacity']),
                        dccap : this.state.dccap.push(zo['dccapacity']),
                        totalrows : this.state.totalrows.push(zo['totalrows'])
                    })
                }
            )}
            ).then(hello1 => console.log(this.state));

        return(
            <div>
                <FinalNav fname={this.props.fname}
                          role={this.props.role}
                          plants={this.props.plants}
                          pr={this.pr}
                          handler1={this.handler2}
                          number={this.state.zone}
                          plantstats={false}
                          handle={this.handler3}
                          onCardClicked={this.onCardClicked}
                          setplant={this.setPlant}
                />
                <div id='least' className='fill'>
                    <Material_Card id='1'
                        zone={1}
                        rows={280}
                        today={"26/06/2019"}
                        accap={2.76}
                        dccap={3.04}
                        progress={37}
                        onCardClicked={this.onCardClicked}
                    />
                    <Material_Card id='2'
                        zone={2}
                        rows={266}
                        today={"26/06/2019"}
                        accap={2.76}
                        dccap={3.04}
                        progress={13}
                        onCardClicked={this.onCardClicked}
                    />
                    <Material_Card id='3'
                        zone={3}
                        rows={190}
                        today={"26/06/2019"}
                        accap={1.863}
                        dccap={2.15}
                        progress={90}
                        onCardClicked={this.onCardClicked}
                    />
                    <Material_Card id='4'
                        zone={4}
                        rows={265}
                        today={"26/06/2019"}
                        accap={2.76}
                        dccap={3.31}
                        progress={47}
                        onCardClicked={this.onCardClicked}
                    />
                </div>
            </div>
        );
    }

    onNumberFixed(event) {
        this.setState({
            zone : Number(event.target.value),
        });
    }

    onCycleFixed(event) {
        this.setState({
            current : Number(event.target.value),
        });
    }

    onRowFixed(event) {
        this.setState({
            row : Number(event.target.value),
        });
    }

    onCardClicked() {
        fetch('http://192.168.64.2/web_poc/getblockdata.php',{
            method : 'post',
            headers :{
                'Content-Type' : 'application/json',
            },
            body :JSON.stringify({
                contractyear : 2018,
                cycle : 5,
                zone : 1,
                blockname : 'A1',
            }),
        });
    }

}

export default withRouter(ZoneStats);
