import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import $ from 'jquery';
import Popper from 'popper.js';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import './textfields.css';

class TextFields extends Component{
    constructor(props) {
        super(props);
        this.state =
            {
                UsernameChange : "",
                passwordChange : ""
            };
    }

    render() {
        return (
            <form className='hello'>
                <div className="form-group">
                    <label htmlFor="exampleInputEmail1">Username</label> <br />
                    <input type="text"
                           name="username"
                           id="exampleInputEmail1"
                           placeholder="Enter Username"
                           onChange={this.onEmailChange}
                    />
                    <br />
                    <label htmlFor="exampleInputPassword1">Password</label><br />
                    <input type="password"
                           name="password"
                           id="exampleInputPassword1"
                           placeholder="Password"
                           onChange={this.onPasswordChange}
                    />
                    <br />
                    <div className="form-group form-check">
                        <input type="checkbox" className="form-check-input" id="exampleCheck1" />
                        <label className="form-check-label" htmlFor="exampleCheck1">Check me out</label><br />
                    </div>
                    <button onClick={this.onSubmitClicked}
                            type="submit"
                            className="btn btn-outline-success">
                        Submit
                    </button>
                </div>
            </form>
        );
    }

    onEmailChange(event) {
        this.setState(
            {UsernameChange : event.target.value},
        )
    }

    onPasswordChange(event) {
        this.setState(
            {passwordChange : event.target.value},
        )

    }

    onSubmitClicked() {
        console.log(this.state);
        // fetch("http://localhost:3000/signin",
        //     {
        //         method:'post',
        //         headers: {
        //             'Content-Type' : 'application/json',
        //         },
        //         body:{
        //             'username' : this.state.UsernameChange,
        //             'password' : this.state.passwordChange
        //         }
        //     });
    }
}

export default TextFields;
