import React, { Component } from 'react';
import './App.css';
import PlantStats from './Stats/plantstats/plantstats';
import {Route} from "react-router";
import {BrowserRouter, Redirect} from "react-router-dom";
import ZoneStats from "./Stats/zonestats/zonestats";
import RowEnquiry from "./Stats/rowstats/rowenquiry";
import Register from "./TextFields/register";
import NewSignIn from "./TextFields/SigninPage/New_Signin";


class App extends Component {

    constructor(props)
    {
        super(props);
        this.state = {
            name : '',
            role : '',
            plants : '',
            signedIn : false,
            plant : "",
        };
        this.handler = this.handler.bind(this);
        this.handler1 = this.handler1.bind(this);
    }

    handler(fname, role, plants){
        this.setState({
            name: fname,
            role : role,
            plants : plants,
            signedIn : true,
        });
    }

    handler1(plant){
        this.setState({
            plant : plant,
        })
    }

    render() {
    return (
      <div className="App">
          {
                <BrowserRouter>
                    <Route exact path="/" render={() => (<Redirect to="/signin" />)} />
                    <Route path="/plantstats" render={(props) => <PlantStats handler={this.handler1} signedIn={this.state.signedIn} fname={this.state.name} role={this.state.role} plants={this.state.plants} {...props}/>}/>
                    <Route path="/zonestats" render={(props) => <ZoneStats handler={this.handler1} signedIn={this.state.signedIn} fname={this.state.name} role={this.state.role} plants={this.state.plants} {...props}/>} />
                    <Route path="/rowstats" render={(props) => <RowEnquiry handler={this.handler1} signedIn={this.state.signedIn} fname={this.state.name} role={this.state.role} plants={this.state.plants} {...props}/>} />
                    <Route path="/register" component={Register} />
                    <Route path='/signin' render={(props) => <NewSignIn handler={this.handler} {...props}/>} />
                    <Route path='/card' component={Material_Card}/>
              </BrowserRouter>
          }
      </div>
    );
  }
}

export default App;
