import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import $ from 'jquery';
import Popper from 'popper.js';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import './plantstats.css'
import FinalNav from "../../Navigation/FinalNav";

class PlantStats extends Component{

    constructor(props) {
        super(props);
        this.state = {
            number: 14,
            current: 0,
            startDate: '16/10/2019',
            endDate: '1/11/2019',
            width: window.innerWidth,
        };
        this.handleResize = this.handleResize.bind(this);
        this.onChange = this.onChange.bind(this);
    }

    pr = {
        cycle : `<div><span>Current Cycle - </span><strong>${this.props.number}</strong></div>`,
        select : ``,
        startDate : "<div><span>StartDate - </span><strong>"+this.props.startDate+"</strong></div>",
        endDate : "<div><span>ExpectedEndDate - </span><strong>"+this.props.endDate+"</strong></div>",
    };

    onChange(){
        if(this.state.width < 991)
        {
            let button = document.querySelector('#sidebar');
            button.style.top = "70px";
        }
        else
        {
            let button = document.querySelector('#sidebar');
            button.style.top = "74px";
        }
    }

    componentDidMount() {
        window.addEventListener('resize', this.handleResize);
        console.log(this.props);
    }
    componentWillUnmount() {
        window.removeEventListener('resize', this.handleResize)
    }


    render() {
        let items = [];
        for (let i=1;i<this.state.number;i++) {
            items.push(<option value={i}>{i}</option>);
        }
        items.push(<option value={this.state.number} selected>{this.state.number}</option>);
        return(
            <div>
                <FinalNav fname={this.props.fname}
                          role={this.props.role}
                          plants={this.props.plants}
                          pr={this.pr}
                          number={this.state.number}
                          startDate={this.state.startDate}
                          endDate={this.state.endDate}
                          plantstats={true}
                />
                <div id="hello1">
                    <div><span><strong>Select cycle</strong></span><select className="form-control" name='cycleNumber' onChange={this.onCycleFixed}> {items}</select></div><br/>
                    <p>The Graph comes here</p>
                    <canvas id="myGraph">
                    </canvas>
                    <p>{this.state.width}</p>
                </div>
            </div>
        );
    }

    handleResize() {
        this.setState({
            width : window.innerWidth,
        })
    }
}

export default PlantStats;





