import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import 'tachyons';
import FinalNav from "../../Navigation/FinalNav";
import Table from './Table';
import {withRouter} from "react-router";

class ZoneStats extends Component{

    constructor(props) {
        super(props);
        this.state = {
            zone : 1,
            current: 1,
            row : 0,
            totalrows : 80,
        };

        this.onNumberFixed = this.onNumberFixed.bind(this);
        this.onCycleFixed = this.onCycleFixed.bind(this);
        this.onRowFixed = this.onRowFixed.bind(this);
    }

    pr = {
        cycle : "<div><span>Current Zone</span><strong id='hel'>"+ this.props.number +"</strong></div>",
        select : "",
        startDate : "",
    };

    render() {
        let items1 = [];
        for (let i=1;i<=this.state.totalrows;i++) {
            items1.push(<option key={i} value={i}>{i}</option>);
        }
        let items2 = [];
        for (let j=this.state.current;j<=this.state.totalrows;j++) {
            items2.push(<option key={j} value={j}>{j}</option>);
        }
        let items3 = [];
            for(let k=this.state.current; k<=this.state.row;k++){
                items3.push({
                    row: k,
                    cleanedStatus : `The Row ${k} cleaning is in progress.`,
                    progress : `${100-k} %`,
                })
            }
        return(
            <div>
                <FinalNav fname={this.props.fname}
                          role={this.props.role}
                          plants={this.props.plants}
                          pr={this.pr}
                          number={this.state.zone}
                          plantstats={false}
                />
                <div id="hello1">
                    <div className="input-group d-flex justify-content-center">
                        <div className="input-group-prepend">
                            <span className='input-group-text'>Enter the required Zone</span>
                            <input onChange={this.onNumberFixed} placeholder="Enter the Zone Number"/>
                        </div>
                    </div><br />
                    <div className="input-group d-flex justify-content-center">
                        <div className="input-group-prepend">
                            <span className='input-group-text'>Select Starting Row</span>
                            <div className="hello123">
                                <select className="custom-select" name="StartingNumber" onChange={this.onCycleFixed} id="StartingNumber">
                                    {items1}
                                </select>
                            </div>
                        </div>
                    </div><br />
                    <div className="input-group d-flex justify-content-center">
                        <div className="input-group-prepend">
                            <span className='input-group-text'>Select ending Row</span>
                            <div className="hello123">
                                <select className="custom-select" name="EndingNumber" onChange={this.onRowFixed} id="EndingNumber">
                                    {items2}
                                </select>
                            </div>
                        </div>
                    </div><br />
                    <Table tab={items3} start={this.state.current} end={this.state.row}/>
                </div>
            </div>
        );
    }

    onNumberFixed(event) {
        this.setState({
            zone : Number(event.target.value),
        });
    }

    onCycleFixed(event) {
        this.setState({
            current : Number(event.target.value),
        });
    }

    onRowFixed(event) {
        this.setState({
            row : Number(event.target.value),
        });
    }
}

export default withRouter(ZoneStats);
