import React, { Component } from 'react';
import Navigation from './Navigation/Navigation';
import TextFields from './TextFields/textfields';
import Root from './Root/Root'
import './App.css';
import PlantStats from './Stats/plantstats/plantstats';


class App extends Component {

    constructor(props)
    {
        super(props);
        this.state = {
            route: 'signIn',
            signedIn : false,
            text : 'Sign In'
        };
        this.onRouteChange = this.onRouteChange.bind(this);
        this.onRouteChange1 = this.onRouteChange1.bind(this);
    }

    onRouteChange(name) {
        this.setState({
           route : name,
        });
        if(this.state.signedIn){
            this.setState({
                text : 'Sign Out',
            })
        }
        else
        {
            this.setState({
                text :'Sign In',
            })
        }
    }

    onRouteChange1(name) {
        this.setState({
           route : name,
            signedIn: true,
            text: 'Sign Out',
        });
    }

    render() {
    return (
      <div className="App">
          <Navigation />
      </div>
    );
  }
}

export default App;
