import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import $ from 'jquery';
import Popper from 'popper.js';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import 'tachyons';
import Card from "../../Card/card";
import ZoneCard from "../../Card/zoneCard";
import Navigation from "../../Navigation/Navigation";
import FinalNav from "../../Navigation/FinalNav";

class ZoneStats extends Component{

    constructor(props) {
        super(props);
        this.state = {
            zone : 1,
            current : 1,
            row : 0,
            startDate : null,
            endDate : null,
            name: 'Surya'
        };

        this.onNumberFixed = this.onNumberFixed.bind(this);
        this.onCycleFixed = this.onCycleFixed.bind(this);
        this.onRowFixed = this.onRowFixed.bind(this);
        this.onStartDateFixed = this.onStartDateFixed.bind(this);
        this.onEndDateFixed = this.onEndDateFixed.bind(this);
    }

    pr = {
        cycle : "<div><span>Current Zone</span><strong id='hel'>"+ this.props.number +"</strong></div>",
        select : "<div><span>Select Zone</span><select name='cycleNumber' onChange="+ this.props.onCycleFixed +" > "+ this.props.items +"</select></div>",
        startDate : "",
    };


    render() {
        let items = [];
        for (let i=1;i<=this.state.zone;i++) {
            items.push(<option value={i}>{i}</option>);
        }
        let items1 = [];
        for (let i=1;i<=40;i++) {
            items1.push(<option value={i}>{i}</option>);
        }
        return(
            <div>
                <FinalNav name={this.state.name} number={this.state.zone} pr={this.pr}/>
                <div id="hello1">
                    <div className="input-group d-flex justify-content-center">
                        <div className="input-group-prepend">
                            <span className='input-group-text'>Enter the required Zone</span>
                            <input onChange={this.onNumberFixed} placeholder="Enter the Zone Number"/>
                        </div>
                    </div><br />
                    <div className="input-group d-flex justify-content-center">
                        <div className="input-group-prepend">
                            <span className='input-group-text'>Select Zone</span>
                            <div className="hello123">
                                <select className="custom-select" name="cycleNumber" onChange={this.onCycleFixed} id="cycleNumber">
                                    {items}
                                </select>
                            </div>
                        </div>
                    </div><br />
                    <div className="input-group d-flex justify-content-center">
                        <div className="input-group-prepend">
                            <span className='input-group-text'>Select Row</span>
                            <div className="hello123">
                                <select className="custom-select" name="cycleNumber" onChange={this.onRowFixed} id="cycleNumber">
                                    {items1}
                                </select>
                            </div>
                        </div>
                    </div><br />
                    <ZoneCard name={this.state.current}/>
                    <ZoneCard name={this.state.current}/>
                    <ZoneCard name={this.state.current}/>
                    <ZoneCard name={this.state.current}/>
                </div>
             </div>
        );
    }

    onNumberFixed(event) {
        this.setState({
            zone : event.target.value,
        });
    }

    onCycleFixed(event) {
        this.setState({
            current : event.target.value,
        });
    }

    onRowFixed(event) {
        this.setState({
            row : event.target.value,
        });
    }

    onStartDateFixed(event) {
        this.setState({
            startDate : event.target.value,
        });
    }

    onEndDateFixed(event) {
        this.setState({
            endDate : event.target.value,
        });
    }



}

export default ZoneStats;
