import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import $ from 'jquery';
import Popper from 'popper.js';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import './plantstats.css';
import 'tachyons';

class Rowstats extends Component{

    constructor(props) {
        super(props);
        this.state = {
            number : 11,
            current : 0,
            startDate : null,
            endDate : null
        };

        this.onNumberFixed = this.onNumberFixed.bind(this);
        this.onCycleFixed = this.onCycleFixed.bind(this);
        this.onStartDateFixed = this.onStartDateFixed.bind(this);
        this.onEndDateFixed = this.onEndDateFixed.bind(this);
    }


    render() {
        let items = [];
        for (let i=1;i<=this.state.number;i++) {
            items.push(<option value={i}>{i}</option>);
        }
        return(
            <div id="hello1">
                <div className="input-group">
                    <div className="input-group-prepend">
                        <span className='input-group-text'>Current Cycle</span>
                    </div>
                    <strong id="hel">11</strong>
                </div><br />
                <div className="input-group">
                    <div className="input-group-prepend">
                        <span className='input-group-text'>Select cycle</span>
                    </div>
                    <div className="hello123">
                        <select className="custom-select" name="cycleNumber" onChange={this.onCycleFixed} id="cycleNumber">
                            {items}
                        </select>
                    </div>
                </div><br />
                <canvas id="myGraph"></canvas>
                <div className="input-group">
                    <div className="input-group-prepend">
                        <span className='input-group-text'>StartDate</span>
                    </div>
                    <strong id="hel">01/06/2019</strong>
                </div><br />
                <div className="input-group">
                    <div className="input-group-prepend">
                        <span className='input-group-text'>EndDate</span>
                    </div>
                    <strong id="hel">15/06/2019</strong>
                </div>
            </div>
        );
    }

    onNumberFixed(event) {
        this.setState({
            number : event.target.value,
        });
    }

    onCycleFixed(event) {
        this.setState({
            current : event.target.value,
        });
    }

    onStartDateFixed(event) {
        this.setState({
            startDate : event.target.value,
        })
    }
    onEndDateFixed(event) {
        this.setState({
            endDate : event.target.value,
        })
    }



}

export default Rowstats;
