import React, { Component } from 'react';
import TextFields from './TextFields/textfields';
import './App.css';
import PlantStats from './Stats/plantstats/plantstats';
import {Route} from "react-router";
import {BrowserRouter, Redirect} from "react-router-dom";
import ZoneStats from "./Stats/zonestats/zonestats";
import Rowstats from "./Stats/rowstats/rowstats";
import RowEnquiry from "./Stats/rowstats/rowenquiry";
import Register from "./TextFields/register";


class App extends Component {

    constructor(props)
    {
        super(props);
        this.state = {
            name : '',
            role : '',
            plants : ''
        };
        this.handler = this.handler.bind(this);
    }

    handler(fname, role, plants){
        this.setState({
            name: fname,
            role : role,
            plants : plants,
        })
    }

    render() {
    return (
      <div className="App">
          {
                <BrowserRouter>
                    <Route exact path="/" render={() => (<Redirect to="/signin" />)} />
                    <Route path="/signin" render={(props) => <TextFields handler={this.handler} {...props}/>} />
                    <Route path="/plantstats" fname={this.state.name} role={this.state.role} plants={this.state.plants} component={PlantStats}/>
                    <Route path="/zonestats" fname={this.state.name} role={this.state.role} plants={this.state.plants} component={ZoneStats} />
                    <Route path="/rowstats" fname={this.state.name} role={this.state.role} plants={this.state.plants} component={Rowstats} />
                    <Route path="/rowenquiry" fname={this.state.name} role={this.state.role} plants={this.state.plants} component={RowEnquiry} />
                    <Route path="/register" component={Register} />
              </BrowserRouter>
          }
      </div>
    );
  }
}

export default App;
