import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import $ from 'jquery';
import Popper from 'popper.js';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import './textfields.css';
import http from 'http';

class TextFields extends Component{
    constructor(props) {
        super(props);
        this.state =
            {
                UsernameChange : "",
                passwordChange : ""
            };
        this.onEmailChange = this.onEmailChange.bind(this);
        this.onPasswordChange = this.onPasswordChange.bind(this);
        this.onSubmitClicked = this.onSubmitClicked.bind(this);
    }

    render() {
        return (
            <form className='hello'>
                <div className="form-group">
                    <label htmlFor="exampleInputEmail1">Username</label> <br />
                    <input type="text"
                           name="username"
                           className="form-control"
                           id="exampleInputEmail1"
                           aria-describedby="emailHelp"
                           placeholder="Enter Username"
                           onChange={this.onEmailChange}
                    />
                    <br />
                    <label htmlFor="exampleInputPassword1">Password</label><br />
                    <input type="password"
                           name="password"
                           className="form-control"
                           id="exampleInputPassword1"
                           placeholder="Password"
                           onChange={this.onPasswordChange}
                    />
                    <br />
                    <input type="submit"
                           className="btn btn-outline-success"
                           value="Sign In"
                           onClick={this.onSubmitClicked}
                    />
                </div>
            </form>
        );
    }

    onEmailChange(event) {
        this.setState(
            {UsernameChange : event.target.value},
        );
    }

    onPasswordChange(event) {
        this.setState(
            {passwordChange : event.target.value},
        );
    }

    onSubmitClicked = () => {
        // fetch('http://localhost:3000/signin',
        //     {
        //         method: "post",
        //         headers :{
        //             "Content-Type" : 'application/json',
        //         },
        //         body: JSON.stringify({
        //             username : this.state.UsernameChange,
        //             password : this.state.passwordChange
        //         })
        //     })
        //     .then(resp => resp.json())
        //     .then(data => {
        //         if(data['status'] === 'success')
        //             {
        //                 this.props.onRouteChange();
        //             }
        // });
        this.props.onRouteChange();
    }
}

export default TextFields;


