import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import 'tachyons';
import FinalNav from "../../Navigation/FinalNav";
import Table from './Table';

class ZoneStats extends Component{

    constructor(props) {
        super(props);
        this.state = {
            zone : 1,
            current: '10',
            row : '15',
            totalrows : '40',
            name: 'Surya'
        };

        this.onNumberFixed = this.onNumberFixed.bind(this);
        this.onCycleFixed = this.onCycleFixed.bind(this);
        this.onRowFixed = this.onRowFixed.bind(this);
    }

    pr = {
        cycle : "<div><span>Current Zone</span><strong id='hel'>"+ this.props.number +"</strong></div>",
        select : "",
        startDate : "",
    };

    componentDidUpdate(prevProps, prevState, snapshot) {
        console.log(this.state);
    }

    render() {
        let items = [];
        for (let i=1;i<=this.state.zone;i++) {
            items.push(<option key={i} value={i}>{i.toString()}</option>);
        }
        let items1 = [];
        for (let i=1;i<=this.state.totalrows;i++) {
            items1.push(<option key={i} value={i}>{i.toString()}</option>);
        }
        let items2 = [];
        for (let i=this.state.current;i<=this.state.totalrows;i++) {
            items2.push(<option key={i} value={i}>{i.toString()}</option>);
        }
        let items3 = [];
            for(let i=this.state.current; i<=this.state.row;i++){
                items3.push({
                    row: i.toString(),
                    cleanedStatus : `The Row ${i} cleaning is in progress.`,
                    progress : 100-i,
                })
            }
        return(
            <div>
                <FinalNav name={this.state.name} row={this.state.row} number={this.state.zone} pr={this.pr} plantstats={false}/>
                <div id="hello1">
                    <div className="input-group d-flex justify-content-center">
                        <div className="input-group-prepend">
                            <span className='input-group-text'>Enter the required Zone</span>
                            <input onChange={this.onNumberFixed} placeholder="Enter the Zone Number"/>
                        </div>
                    </div><br />
                    <div className="input-group d-flex justify-content-center">
                        <div className="input-group-prepend">
                            <span className='input-group-text'>Select Starting Row</span>
                            <div className="hello123">
                                <select className="custom-select" name="StartingNumber" onChange={this.onCycleFixed} id="StartingNumber">
                                    {items1}
                                </select>
                            </div>
                        </div>
                    </div><br />
                    <div className="input-group d-flex justify-content-center">
                        <div className="input-group-prepend">
                            <span className='input-group-text'>Select ending Row</span>
                            <div className="hello123">
                                <select className="custom-select" name="EndingNumber" onChange={this.onRowFixed} id="EndingNumber">
                                    {items2}
                                </select>
                            </div>
                        </div>
                    </div><br />
                    <Table tab={items3} />
                </div>
            </div>
        );
    }

    onNumberFixed(event) {
        this.setState({
            zone : event.target.value,
        });
    }

    onCycleFixed(event) {
        this.setState({
            current : event.target.value,
        });
    }

    onRowFixed(event) {
        this.setState({
            row : event.target.value.toString(),
        });
    }



}

export default ZoneStats;
