import React, { Component } from 'react';
import './table.css';
class Table extends Component {

    constructor(props) {
        super(props);
        this.state = {
            status : [],
        }
    }

    componentDidUpdate(prevProps, prevState, snapshot) {
        if(prevProps !== this.props)
        {
            fetch('http://192.168.64.2/this.php',
                {
                    method: "POST",
                    headers :
                        {
                            'Content-Type' : 'application/x-www-form-urlencoded',
                        },
                    body : `start=${this.props.start}&end=${this.props.end}`,
                })
                .then( res => res.json())
                .then(data => this.setState({
                    status : data
                }))
                .catch(err => console.log(err));
        }
    }

    renderTableData()
    {
        console.log(this.state);
        if(this.state.status.length){
            return this.state.status.map((tab, index) => {
                const { row, cleanstatus, progress } = tab; //destructuring
                return (
                    <tr key={row}>
                        <td>{row}</td>
                        <td>{cleanstatus}</td>
                        <td>{progress}</td>
                    </tr>
                )
            })
        }
        else
        {
            return (
                <tr key='1'>
                    <td>Select the</td>
                    <td>Starting and</td>
                    <td>Ending Row</td>
                </tr>
            )
        }
    }

    renderTableHeader() {
        // console.log(this.props.tab);
        return (
            <tr>
                <th>Row</th>
                <th>Clean Status</th>
                <th>Progress</th>
            </tr>
        )
    }

    render() {
        return (
            <div>
                <h1 id='title'>Row Info</h1>
                <table id='RvZ'>
                    <tbody>
                    {this.renderTableHeader()}
                        {this.renderTableData()}
                    </tbody>
                </table>
            </div>
        )
    }
}

export default Table;
