import React from 'react';
import { QRCode } from 'react-qr-svg';
import './QR.css';
import html2canvas from 'html2canvas';
import $ from 'jquery';
import 'jquery.qrcode/src/jquery.qrcode';
import 'jquery.qrcode/src/qrcode';

const styles = {
    root: {
        fontFamily: 'sans-serif',
    },
    h1: {
        textAlign: 'center',
    },
    qrcode1: {
        textAlign: 'center',
    },
};

export default class QRCode_Gen extends React.Component {

    constructor(props) {
        super(props);
        this.state =
            {
                zone: 0,
                row: 0,
                block: '',
                inverter: 0.0,
                SMB: '',
            };

        this.onEmailChange = this.onEmailChange.bind(this);
        this.onPasswordChange = this.onPasswordChange.bind(this);
        this.onBlockChange = this.onBlockChange.bind(this);
        this.onInverterChange = this.onInverterChange.bind(this);
        this.onSMBChange = this.onSMBChange.bind(this);
        this.onSMBChange = this.onSMBChange.bind(this);
        this.onButtonClicked = this.onButtonClicked.bind(this);
        this.validate = this.validate.bind(this);
        this.func = this.func.bind(this);
    }

    componentDidMount() {
        $('#hellll').qrcode(
            {
                text : JSON.stringify({
                    zone: this.state.zone,
                    row_num: this.state.row,
                    blockname: this.state.block,
                    inverter: this.state.inverter,
                    smb: this.state.SMB,
                }),
                mode : 2,
                mSize: 0.1,
                mPosX: 0.5,
                mPosY: 0.5,
                label: `Z ${this.state.zone} R ${this.state.row} B ${this.state.block}`,
                size : 300,
                ecLevel : 'H',
            })
    }

    render() {
        return (
            <div>
                <div id='bel'>
                    <form className='hello'>
                        <div className="form-group">
                            <label htmlFor="exampleInputEmail1">Zone Number</label> <br/>
                            <input type="number"
                                   name="zone"
                                   className="form-control"
                                   id="exampleInputEmail1"
                                   aria-describedby="emailHelp"
                                   placeholder="Enter Zone Number"
                                   onChange={this.onEmailChange}
                            />
                            <br/>
                            <label htmlFor="exampleInputPassword1">Row Number</label><br/>
                            <input type="number"
                                   name="row"
                                   className="form-control"
                                   id="exampleInputPassword1"
                                   placeholder="Enter Row Number"
                                   onChange={this.onPasswordChange}
                            />
                            <br/>
                            <label htmlFor="exampleInputPassword1">Block Number</label><br/>
                            <input type="text"
                                   name="block"
                                   className="form-control"
                                   id="exampleInputPassword1"
                                   placeholder="Enter Block Number"
                                   onChange={this.onBlockChange}
                            />
                            <br/>
                            <label htmlFor="exampleInputPassword1">Inverter ID</label><br/>
                            <input type="number"
                                   name="inverter"
                                   className="form-control"
                                   id="exampleInputPassword1"
                                   placeholder="Enter Inverter ID"
                                   onChange={this.onInverterChange}
                            />
                            <br/>
                            <label htmlFor="exampleInputPassword1">SMB ID</label><br/>
                            <input type="text"
                                   name="SMB"
                                   className="form-control"
                                   id="exampleInputPassword1"
                                   placeholder="Enter SMB ID"
                                   onChange={this.onSMBChange}
                            />
                            <br/>
                            <button onClick={this.onButtonClicked} className="btn btn-success button" id="rowstats">Get
                                the status
                            </button>
                        </div>
                    </form>
                </div>
                <div id='jel' className='hell' style={styles.root}>
                    <h1 style={styles.h1}>QRCode with JSON</h1>
                    <canvas id='hellll' style={styles.qrcode1}></canvas>
                    <div id='but'>
                        <a id='download' download='hello.png' className='btn btn-success' onClick={this.func}>Download as PNG</a>
                        <button className='btn btn-dark' onClick={this.onButtonClicked}>Get QR for another row</button>
                    </div>
                </div>
            </div>
        );
    }

    onButtonClicked(e) {
        e.preventDefault();
        if (!this.validate()) {
            document.getElementById('bel').classList.toggle('hell');
            document.getElementById('jel').classList.toggle('hell');
        } else {
            alert('All the fields are required.');
        }
    }

    validate() {
        return this.state.zone === 0 && this.state.row === 0 && this.state.block === '' && this.state.inverter === 0.0 && this.state.SMB === '';
    }

    onEmailChange(event) {
        this.setState(
            {zone: Number(event.target.value)},
        );
    }

    onPasswordChange(event) {
        this.setState(
            {row: Number(event.target.value)},
        );
    }

    onBlockChange(event) {
        this.setState(
            {block: event.target.value.toString()},
        );
    }

    onInverterChange(event) {
        this.setState(
            {inverter: Number(event.target.value)},
        );
    }

    onSMBChange(event) {
        this.setState(
            {SMB: event.target.value.toString()},
        );
    }

}
