import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import $ from 'jquery';
import Popper from 'popper.js';
import 'bootstrap/dist/js/bootstrap.bundle.min';

class PlantStats extends Component{

    constructor(props) {
        super(props);
        this.state = {
            number : 0,
            current : 0,
            startDate : null,
            endDate : null
        };

        this.onNumberFixed = this.onNumberFixed.bind(this);
        this.onCycleFixed = this.onCycleFixed.bind(this);
        this.onStartDateFixed = this.onStartDateFixed.bind(this);
        this.onEndDateFixed = this.onEndDateFixed.bind(this);
    }


    render() {
        let items = [];
        for (let i=1;i<=this.state.number;i++) {
            items.push(<option value={i}>{i}</option>);
        }
        return(
            <div>
                <div className="input-group d-flex justify-content-center">
                    <div className="input-group-prepend">
                        <span className='input-group-text'>Current Cycle</span>
                    </div>
                    <input type='number' name="currentCycle" onChange={this.onNumberFixed} id="currentCycle" />
                </div><br />
                <div className="input-group d-flex justify-content-center">
                    <div className="input-group-prepend">
                        <span className='input-group-text'>Select cycle</span>
                    </div>
                    <select name="cycleNumber" onChange={this.onCycleFixed} id="cycleNumber">
                        {items}
                    </select>
                </div><br />
                <canvas id="myGraph"></canvas>
                <div className="input-group d-flex justify-content-center">
                    <div className="input-group-prepend">
                        <span className='input-group-text'>StartDate</span>
                    </div>
                    <input type='date' name="startDate" onChange={this.onStartDateFixed} id="startDate" />
                </div><br />
                <div className="input-group d-flex justify-content-center">
                    <div className="input-group-prepend">
                        <span className='input-group-text'>EndDate</span>
                    </div>
                    <input type='date' name="startDate" onChange={this.onEndDateFixed} id="endDate" />
                </div>
            </div>
        );
    }

    onNumberFixed(event) {
        this.setState({
            number : event.target.value,
        });
    }

    onCycleFixed(event) {
        this.setState({
            current : event.target.value,
        });
        console.log(this.state);
    }

    onStartDateFixed(event) {
        this.setState({
            startDate : event.target.value,
        })
    }
    onEndDateFixed(event) {
        this.setState({
            endDate : event.target.value,
        })
    }

    createSelectItems() {
        let items = [];
        for (let i = 1; i <= this.state.number; i++) {
            items.push(<option key={i} value={i}>{i}</option>);
        }
        console.log(items);
        return items;
    }



}

export default PlantStats;
