import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import 'tachyons';
import FinalNav from "../../Navigation/FinalNav";
import Table from './Table';
import {Redirect, withRouter} from "react-router";
import Material_Card from '../../Card/Material_Card';
import './plantstats.css'

class ZoneStats extends Component{

    constructor(props) {
        super(props);
        this.state = {
            zone : 1,
            current: 1,
            row : 0,
            totalrows : 80,
            redirect : false,
        };

        this.onNumberFixed = this.onNumberFixed.bind(this);
        this.onCycleFixed = this.onCycleFixed.bind(this);
        this.onRowFixed = this.onRowFixed.bind(this);
        this.handler2 = this.handler2.bind(this);
    }

    pr = {
        cycle : "<div><span>Current Zone </span><strong>"+ this.props.number +"</strong></div>",
        select : "",
        startDate : "",
    };

    componentDidMount() {
        if(!this.props.signedIn){
            this.setState({
                redirect : true,
            });
        }
    }

    handler2(number){
        this.setState({
            number: number,
        });
    }

    handler3(){
        let body = document.getElementById('least');
        body.classList.toggle('fill');
    }

    render() {
        if(this.state.redirect){
            alert("SignIn to Access the Info");
            return <Redirect to='/signin'/>
        }
        let items1 = [];
        for (let i=1;i<=this.state.totalrows;i++) {
            items1.push(<option key={i} value={i}>{i}</option>);
        }
        let items2 = [];
        for (let j=this.state.current;j<=this.state.totalrows;j++) {
            items2.push(<option key={j} value={j}>{j}</option>);
        }
        return(
            <div>
                <FinalNav fname={this.props.fname}
                          role={this.props.role}
                          plants={this.props.plants}
                          pr={this.pr}
                          handler1={this.handler2}
                          number={this.state.zone}
                          plantstats={false}
                          handle={this.handler3}
                />
                <div id='least' className='fill'>
                    <Material_Card
                        zone={1}
                        rows={280}
                        today={"26/06/2019"}
                        accap={2.76}
                        dccap={3.04}
                        progress={37}
                    />
                    <Material_Card
                        zone={2}
                        rows={266}
                        today={"26/06/2019"}
                        accap={2.76}
                        dccap={3.04}
                        progress={13}
                    />
                    <Material_Card
                        zone={3}
                        rows={190}
                        today={"26/06/2019"}
                        accap={1.863}
                        dccap={2.15}
                        progress={90}
                    />
                    <Material_Card
                        zone={4}
                        rows={265}
                        today={"26/06/2019"}
                        accap={2.76}
                        dccap={3.31}
                        progress={47}
                    />
                </div>
            </div>
        );
    }

    onNumberFixed(event) {
        this.setState({
            zone : Number(event.target.value),
        });
    }

    onCycleFixed(event) {
        this.setState({
            current : Number(event.target.value),
        });
    }

    onRowFixed(event) {
        this.setState({
            row : Number(event.target.value),
        });
    }
}

export default withRouter(ZoneStats);
