import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import 'tachyons';
import FinalNav from "../../Navigation/FinalNav";
import Table from './Table';
import {Redirect, withRouter} from "react-router";
import Material_Card from '../../Card/Material_Card';
import './plantstats.css'

class ZoneStats extends Component{

    constructor(props) {
        super(props);
        this.state = {
            zone : [],
            cycle : 0,
            current: 1,
            row : 0,
            totalrows : [],
            redirect : false,
            plant : this.props.plants.split('&')[0].toLowerCase(),
            accap : [],
            dccap : [],
            totalzones : 0,
        };

        this.onNumberFixed = this.onNumberFixed.bind(this);
        this.onCycleFixed = this.onCycleFixed.bind(this);
        this.onRowFixed = this.onRowFixed.bind(this);
        this.handler2 = this.handler2.bind(this);
        this.setPlant = this.setPlant.bind(this);
        this.onCardClicked = this.onCardClicked.bind(this);
        this.setStateSynchronous = this.setStateSynchronous.bind(this);
    }

    setStateSynchronous(stateUpdate) {
        return new Promise(resolve => {
            this.setState(stateUpdate, () => resolve());
        });
    }

    pr = {
        cycle : "",
        select : "",
        startDate : "",
    };

    componentDidMount() {
        if(!this.props.signedIn){
            this.setState({
                redirect : true,
            });
        }

        fetch("http://localhost:3000/get-cycle-data",{
            method : 'post',
            headers : {
                'Content-Type' : 'application/json',
            },
            body : JSON.stringify({
                plant : this.state.plant,
            }),
        })
            .then(res => res.json())
            .then(result => {
                this.setState({
                    cycle : result.currentCycle,
                });
            });
        console.log('Im Here');
        fetch('http://localhost:3000/zonedata',{
            method : 'post',
            headers : {
                'Content-Type' : 'application/json',
            },
            body: JSON.stringify({
                plant : this.state.plant,
            })
        }).then(resp => resp.json())
            .then(hello => {
                let zones = [];
                let dccaps = [];
                let acacps = [];
                let tots = [];
                hello.map((zo, i) => {
                    zones.push(zo['zone']);
                    acacps.push(zo['accapacity']);
                    dccaps.push(zo['dccapacity']);
                    tots.push(zo['totalrows']);
                });
                        this.setState({
                            zone : zones,
                            accap : acacps,
                            dccap : dccaps,
                            totalrows : tots,
                            totalzones : hello.length,
                        });
                    }
            );
    }

    handler2(number){
        this.setState({
            number: number,
        });
    }

    setPlant(plant){
        this.setState({
            plant : plant,
        });
    }

    handler3(){
        let body = document.getElementById('least');
        body.classList.toggle('fill');
    }

    render() {
        if(this.state.redirect){
            alert("SignIn to Access the Info");
            return <Redirect to='/signin'/>
        }
        let items1 = [];
        for (let i=1;i<=this.state.totalrows;i++) {
            items1.push(<option key={i} value={i}>{i}</option>);
        }
        let items2 = [];
        for (let j=this.state.current;j<=this.state.totalrows;j++) {
            items2.push(<option key={j} value={j}>{j}</option>);
        }

        let today = new Date();
        let date = ('0' + today.getDate()).slice(-2) + '/' + ('0' + (today.getMonth()+1)).slice(-2) + '/' + today.getFullYear();
        let cards = [];
        for(let i=0;i<this.state.totalzones;i++){
            let random = Math.floor(Math.random() * (100));
            cards.push(<Material_Card zone={this.state.zone[i]}
                                      rows={this.state.totalrows[i]}
                                      today={date}
                                      accap={this.state.accap[i]}
                                      dccap={this.state.dccap[i]}
                                      progress={random}
                                      onCardClicked={this.onCardClicked}
            />);
        }

        return(
            <div>
                <FinalNav fname={this.props.fname}
                          role={this.props.role}
                          plants={this.props.plants}
                          pr={this.pr}
                          handler1={this.handler2}
                          number={this.state.zone}
                          plantstats={false}
                          handle={this.handler3}
                          onCardClicked={this.onCardClicked}
                          setplant={this.setPlant}
                />
                <div id='least' className='fill'>
                    {cards}
                </div>
            </div>
        );
    }

    onNumberFixed(event) {
        this.setState({
            zone : Number(event.target.value),
        });
    }

    onCycleFixed(event) {
        this.setState({
            current : Number(event.target.value),
        });
    }

    onRowFixed(event) {
        this.setState({
            row : Number(event.target.value),
        });
    }

    onCardClicked(int) {
        fetch('http://192.168.64.2/web_poc/getblockdata.php',{
            method : 'post',
            headers :{
                'Content-Type' : 'application/json',
            },
            body :JSON.stringify({
                contractyear : 2018,
                cycle : 5,
                zone : int,
            }),
        });
    }

}

export default withRouter(ZoneStats);
