import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import $ from 'jquery';
import Popper from 'popper.js';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import './plantstats.css'
import FinalNav from "../../Navigation/FinalNav";
import {Redirect, withRouter} from "react-router";

class PlantStats extends Component{

    constructor(props) {
        super(props);
        this.state = {
            number: 14,
            current: 0,
            startDate: '16/10/2019',
            endDate: '1/11/2019',
            redirect : false,
            plant : this.props.plants.split('&')[0].toLowerCase(),
        };
        this.onCycleFixed = this.onCycleFixed.bind(this);
        this.setStateSynchronous = this.setStateSynchronous.bind(this);
        this.handler2 = this.handler2.bind(this);
        this.handler3 = this.handler3.bind(this);
    }

    setStateSynchronous(stateUpdate) {
        return new Promise(resolve => {
            this.setState(stateUpdate, () => resolve());
        });
    }

    pr = {
        cycle : `<div><span>Current Cycle - </span><strong>${this.props.number}</strong></div>`,
        select : ``,
        startDate : "<div><span>StartDate - </span><strong>"+this.props.startDate+"</strong></div>",
        endDate : "<div><span>ExpectedEndDate - </span><strong>"+this.props.endDate+"</strong></div>",
    };

    componentDidMount() {
        if (!this.props.signedIn) {
            this.setState({
                redirect: true,
            });
        }
    }

    handler2(number){
        this.setState({
            number: number,
        });
    }

    handler3(){
        let body = document.getElementById('hello1');
        body.classList.toggle('fill');
    }

    render() {
        let items = [];
        for (let i=1;i<this.state.number;i++) {
            items.push(<option value={i}>{i}</option>);
        }
        items.push(<option value={this.state.number} selected>{this.state.number}</option>);
        if(this.state.redirect){
            alert("SignIn to Access the Info");
            return <Redirect to='/signin'/>
        }

        return(
            <div>
                <FinalNav fname={this.props.fname}
                          role={this.props.role}
                          plants={this.props.plants}
                          pr={this.pr}
                          handler = {this.props.handler}
                          handler1={this.handler2}
                          number={this.state.number}
                          startDate={this.state.startDate}
                          endDate={this.state.endDate}
                          plantstats={true}
                          handle={this.handler3}
                />
                <div id="hello1" className='fill'>
                    <div><span><strong>Select cycle</strong></span><select className="form-control" name='cycleNumber' onChange={this.onCycleFixed}> {items}</select></div><br/>
                    <p>The Graph comes here</p>
                    <canvas id="myGraph">
                    </canvas>
                </div>
            </div>
        );
    }

    onCycleFixed(e) {
        e.preventDefault();
        this.setStateSynchronous(({current: Number(e.target.value)})).then( bro => {
                const date = new Date();
                let date1 = date.getFullYear() + '-' + ('0' + (date.getMonth() + 1)).slice(-2) + '-' + ('0' + date.getDate()).slice(-2);
                console.log(date1);
                fetch('http://localhost:3000/getplant',{
                    method : 'post',
                    headers : {
                        'Content-Type' : 'application/json',
                    },
                    body : JSON.stringify({
                        plant : this.state.plant,
                        startDate : this.state.startDate,
                        presentDate : date1,
                        cycle : this.state.current,
                    })
                })
        }
        )
    }
}

export default withRouter(PlantStats);
