import React,{Component} from 'react';
import PlantForm from './PlantForm';

class CreatePlant extends Component{

    i = 1;
    constructor(props) {
        super(props);
        this.state = {
            blockname : [],
            totalrows : [],
            zone : [],
            inverter : [],
            smb : [],
            _40mrows : [],
            _20mrows : [],
            wp : [],
            totalmodules : [],
            textfields : [],
        };
        this.setStateSynchronous = this.setStateSynchronous.bind(this);
        this.addAnotherRow = this.addAnotherRow.bind(this);
    }

    setStateSynchronous(stateUpdate) {
        return new Promise(resolve => {
            this.setState(stateUpdate, () => resolve());
        });
    }

    addAnotherRow(values){
        const { blockname,totalrows,zone,inverter,smb,_40mrows,_20mrows,wp } = values;
        const a = this.state.blockname;
        const b = this.state.totalrows;
        const c = this.state.zone;
        const d = this.state.inverter;
        const f = this.state.smb;
        const g = this.state._40mrows;
        const h = this.state._20mrows;
        const i = this.state.wp;
        const j = this.state.totalmodules;
        let totalMods = _40mrows*40 + _20mrows*20;

        a.push(blockname);
        b.push(totalrows);
        c.push(zone);
        d.push(inverter);
        f.push(smb);
        g.push(_40mrows);
        h.push(_20mrows);
        i.push(wp);
        j.push(totalMods);

        this.setStateSynchronous({
            blockname : a,
            totalrows : b,
            zone : c,
            inverter : d,
            smb : f,
            _40mrows : g,
            _20mrows : h,
            wp : i,
            totalmodules : j,
        }).then(lotte => {
            this.i += 1;
            let p = this.state.textfields;
            p.push(<PlantForm
                key={this.i}
                AddHandler={this.addAnotherRow}
            />);
            console.log(p);
            let lk = document.getElementById('hello');
            console.log(lk);
            this.setStateSynchronous({
                textfields : p,
            }).then(balloon => {
                console.log(this.state);
            });
        });
    }

    render() {
        return (
            <div id='hello'>
                <div>
                    <PlantForm key={1} AddHandler={this.addAnotherRow}/>
                </div>
                {console.log(this.state.textfields)}
            </div>
        );
    }
}

export default CreatePlant;
