import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import $ from 'jquery';
import Popper from 'popper.js';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import './textfields.css';
import './plantstats.css'
import FinalNav from "../../Navigation/FinalNav";
import {Redirect, withRouter} from "react-router";

class RowEnquiry extends Component{
    constructor(props) {
        super(props);
        this.state =
            {
                number : 1,
                zone : 0,
                row : 0,
                block : '',
                inverter : '',
                SMB: "",
                redirect : false,
                plant : this.props.plants.split('&')[0].toLowerCase(),
                startDate : '',
                endDate: '',
            };
        this.onEmailChange = this.onEmailChange.bind(this);
        this.onPasswordChange = this.onPasswordChange.bind(this);
        this.onBlockChange = this.onBlockChange.bind(this);
        this.onInverterChange = this.onInverterChange.bind(this);
        this.onSMBChange = this.onSMBChange.bind(this);
        this.onButtonClicked = this.onButtonClicked.bind(this);
        this.handler2 = this.handler2.bind(this);
        this.handler3 = this.handler3.bind(this);
        this.setPlant = this.setPlant.bind(this);
    }

    pr = {
        cycle : "",
        select : "",
        startDate : "",
    };

    componentDidMount() {
        if(!this.props.signedIn){
            this.setState({
                redirect : true,
            });
        }

        fetch('http://localhost:3000/get-cycle-data',{
            method : 'post',
            headers : {
                'Content-Type' : 'application/json',
            },
            body : JSON.stringify({
                plant : this.state.plant,
            })
        })
            .then(res=>res.json())
            .then(data=>{
                let { startDate,endDate,currentCycle } = data;
                this.setState({
                    startDate: startDate,
                    endDate: endDate,
                    number: currentCycle,
                });
            })
    }

    handler3(){
        if(document.getElementById('bel').style.display !== 'none'){
            let body = document.getElementById('bel');
            body.classList.toggle('fill');
        }
        let body = document.getElementById('jel');
        body.classList.toggle('fill');
    }

    setPlant(plant){
        this.setState({
            plant : plant,
        });
    }

    handler2(number){
        this.setState({
            number: number,
        });
    }

    render() {
        if(this.state.redirect){
            alert("SignIn to Access the Info");
            return <Redirect to='/signin'/>
        }

        return (
            <div>
                <FinalNav fname={this.props.fname}
                          role={this.props.role}
                          plants={this.props.plants}
                          number={this.state.number}
                          pr={this.pr}
                          handler1={this.handler2}
                          plantstats={false}
                          handle={this.handler3}
                          setplant={this.setPlant}
                />
                <div id='bel' className='fill'>
                    <form className='hello'>
                        <div className="form-group">
                            <label htmlFor="exampleInputEmail1">Zone Number</label> <br />
                            <input type="number"
                                   name="zone"
                                   className="form-control"
                                   id="exampleInputEmail1"
                                   aria-describedby="emailHelp"
                                   placeholder="Enter Zone Number"
                                   onChange={this.onEmailChange}
                            />
                            <br />
                            <label htmlFor="exampleInputPassword1">Row Number</label><br />
                            <input type="number"
                                   name="row"
                                   className="form-control"
                                   id="exampleInputPassword1"
                                   placeholder="Enter Row Number"
                                   onChange={this.onPasswordChange}
                            />
                            <br />
                            <label htmlFor="exampleInputPassword1">Block Name</label><br />
                            <input type="text"
                                   name="block"
                                   className="form-control"
                                   id="exampleInputPassword1"
                                   placeholder="Enter Block Number"
                                   onChange={this.onBlockChange}
                            />
                            <br />
                            <label htmlFor="exampleInputPassword1">Inverter ID</label><br />
                            <input type="number"
                                   name="inverter"
                                   className="form-control"
                                   id="exampleInputPassword1"
                                   placeholder="Enter Inverter ID"
                                   onChange={this.onInverterChange}
                            />
                            <br />
                            <label htmlFor="exampleInputPassword1">SMB ID</label><br />
                            <input type="text"
                                   name="SMB"
                                   className="form-control"
                                   id="exampleInputPassword1"
                                   placeholder="Enter SMB ID"
                                   onChange={this.onSMBChange}
                            />
                            <br />
                            <button onClick={this.onButtonClicked} className="btn btn-success button" id="rowstats">Get the status</button>
                        </div>
                    </form>
                </div>
                <div id='jel' className='hell fill'>
                    <div id="hello1">
                        <div className="input-group">
                            <div className="input-group-prepend">
                                <span className='input-group-text'>Clean Status</span>
                                <em className='hel'><strong>Cleaned</strong></em>
                            </div>
                        </div><br />
                        <div className="input-group">
                            <div className="input-group-prepend">
                                <span className='input-group-text'>Updated Date</span>
                                <strong className="hel">11/06/2019</strong>
                            </div>
                        </div><br />
                        <div className="input-group">
                            <div className="input-group-prepend">
                                <span className='input-group-text'>Updated Time</span>
                                <strong className="hel">11:20</strong>
                            </div>
                        </div><br/>
                        <button className='btn btn-outline-secondary' onClick={this.onButtonClicked}>Check Other Row Status</button>
                    </div>
                </div>
            </div>
        );
    }

    onEmailChange(event) {
        this.setState(
            {zone : Number(event.target.value)},
        );
    }

    onPasswordChange(event) {
        this.setState(
            {row : Number(event.target.value)},
        );
    }

    onBlockChange(event) {
        this.setState(
            {block : event.target.value.toString()},
        );
    }

    onInverterChange(event) {
        this.setState(
            {inverter : event.target.value.toString()},
        );
    }

    onSMBChange(event) {
        this.setState(
            {SMB : event.target.value.toString()},
        );
    }

    onButtonClicked(e){
        e.preventDefault();
        fetch('http://localhost:3000/rowstats',{
            method : 'post',
            headers : {
                'Content-Type' : 'application/json',
            },
            body : JSON.stringify({

            })
        });
        document.getElementById('bel').classList.toggle('hell');
        document.getElementById('jel').classList.toggle('hell');
    }
}

export default withRouter(RowEnquiry);
