import React, {Component, Element} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import $ from 'jquery';
import Popper from 'popper.js';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import FinalTop from './finalTop';
import {Link} from 'react-router-dom';
import './FinalNav.css';

class FinalNav extends Component{

    constructor(props) {
        super(props);
        this.state = {
            plant : 'Kothagadi',
            html1 : "",
        };
        this.OnPlantChange = this.OnPlantChange.bind(this);
        this.SelectCycle = this.SelectCycle.bind(this);
    }

    static Hello(){
            document.getElementById('sidebar').classList.toggle('active');
    }

    SelectCycle(props){
        let html = this.props.pr.cycle;
        let html1 = html.replace('undefined',this.props.number);
        this.setState({
            html1 : html1,
        })
    }

    componentDidMount() {
        setTimeout(this.SelectCycle, 500);
    }


    render(){
        return (
            <div className="wrapper">
                    <button type="button" id="sidebarCollapse" className="btn btn-info fa-navicon" onClick={FinalNav.Hello}>
                        <div className='hellobro'>
                            <span className='bro'/>
                            <span className='bro'/>
                            <span className='bro'/>
                        </div>
                    </button>
                <nav id="sidebar">
                    <div className="sidebar-header">
                        <h3>QR App</h3>
                    </div>
                    <ul className="list-unstyled components">
                        <li className="active">
                            <label htmlFor="sel1">Select Plant:</label>
                            <select onChange={this.OnPlantChange} className="form-control" id="sel1">
                                <option selected disabled >Select Plant</option>
                                <option >Kothagadi</option>
                                <option>Orai</option>
                                <option>Peerampalli</option>
                                <option>Parigi</option>
                                <option>Rohini</option>
                            </select>
                        </li>
                        <li>
                            {/*<a href="#">Zone Stats</a>*/}
                            <Link to='/plantstats'>Plant Stats</Link>
                        </li>
                        <li>
                            {/*<a href="#">Row Stats</a>*/}
                            <Link to='/rowenquiry'>Row Stats</Link>
                        </li>
                        <li>
                            {/*<a href="#">Zone Stats</a>*/}
                            <Link to='/zonestats'>Zone Stats</Link>
                        </li>
                    </ul>

                    <ul className="list-unstyled CTAs">
                        <li>
                            <a href="#" className="download">Logged in as</a>
                        </li>
                        <li>
                            <a href="#" className="article">{this.props.name}</a>
                        </li>
                    </ul>
                    <ul className="list-unstyled components" id='kelo'>
                        <li>
                            <Link to={"/signin"}>Log Out</Link>
                        </li>
                    </ul>
                </nav>
                <div id='hell'>
                    <nav className="navbar navbar-expand-lg navbar-dark" style = {{background : '#7386D5', color : '#fff'}}>
                        <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav"
                                aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                            <span className="navbar-toggler-icon"></span>
                        </button>
                        <div className="collapse navbar-collapse" id="navbarNav">
                            <ul className="navbar-nav">
                                <li className="nav-item active">
                                    <strong className="nav-link" >{this.state.plant}< span className="sr-only">(current)</span></strong>
                                </li>
                                <li className="nav-item active">
                                    <strong className="nav-link" dangerouslySetInnerHTML = {{ __html: this.state.html1 }}></strong>
                                </li>
                                <li className="nav-item active">
                                    <strong className="nav-link" dangerouslySetInnerHTML = {{ __html: this.props.pr.select }}></strong>
                                </li>
                                <li className="nav-item active">
                                    <strong className="nav-link" dangerouslySetInnerHTML = {{ __html: this.props.pr.startDate }}></strong>
                                </li>
                                <li className="nav-item active">
                                    <strong className="nav-link" dangerouslySetInnerHTML = {{ __html: this.props.pr.endDate }}></strong>
                                </li>
                            </ul>
                        </div>
                    </nav>
                </div>
            </div>
        );
    }

    OnPlantChange(event) {
        this.setState({
            plant : event.target.value,
        });
        this.SelectCycle();
    }
}

export default FinalNav;
